INSERT INTO product (id, product_name, product_price, product_image, product_description1, product_description2, product_description3, product_dimensions) VALUES (5, 'Haier portable air conditioner', '$299.99', 'air_condition.jpg', 'If you need a great way to keep yourself cool, and have no central air or not enough window space, the Haier portable air conditioner will do the job', 'This 8,000-BTU portable air conditioner is ideal for spot cooling small rooms up to 250 square feet. With three cool settings, three fan settings, a 24-hour on/off timer and dehumidify mode, this single-hose model is equipped to meet your interior climate needs. A Quick-Install Window Kit is included for exhausting the unit through any horizontal or vertical opening window and fully-directional casters provide mobility. Maintenance is minimal with a slide-out washable mesh filter and Auto Evaporation technology makes manual draining unnecessary.', null, null);
INSERT INTO product (id, product_name, product_price, product_image, product_description1, product_description2, product_description3, product_dimensions) VALUES (6, 'Magic Bullet', '$39.99', 'magic_bullet.jpg', 'The perfect machine for all your smoothie, or blending needs. Easily portable.', 'As Seen on TV Magic Bullet, 17-Piece set:Chops, mixes, blends grinds and more1 high-torque power base2 stainless steel blades1 tall cup1 short cup2 shaker / steamer tops2 stay-fresh resealable lids4 22oz oversized mugs4 colored lip rings for mugs17-piece blender with 100-page recipe book includedDoes kitchen jobs in 10 seconds or less The As Seen on TV Magic Bullet Express Blender and Mixer System 17-Piece Set can do virtually any job in the kitchen in 10 seconds or less. This handy countertop appliance can chop, mix, blend, whip, grind and more. This As Seen on TV blender is ideal for making refreshing smoothies, shakes, zesty salsas and mouthwatering desserts. It features a high-torque power base and stainless steel blades for fast and smooth chopping. This model comes with a tall and a short cup, shaker and steamer tops, stay-fresh resealable lids, oversize mugs and colored lip rings for the mugs. It also comes with a 100-page recipe book.', null, null);
