INSERT INTO product (id, product_name, product_price, product_image, product_description1, product_description2, product_description3, product_dimensions) VALUES (8, 'The Clapper', '$17.99', 'clapper.jpg', 'Great and easy way to turn off electronics without moving from where you are.', 'Control 1 or 2 things at the same time by simply clapping your hands.;Great for lamps and Christmas tree lights! CLAP ON, CLAP OFF;2 CLAPS/3 CLAPS SETTING: connect to the 2 or 3 clap outlet, then activate each one with 2 or 3 claps respectively.;Features Clap Detection Lights: Lights glow in sequence when proper claps are detected. Indicator lights glow when a connected appliance is clapped off.;Home/Away Switch: Adjust for softer or louder claps. In AWAY setting, connected appliances will turn on with the detection of any sound.', null, null);
INSERT INTO product (id, product_name, product_price, product_image, product_description1, product_description2, product_description3, product_dimensions) VALUES (9, 'Kaboom Foam-Tastic Bathroom Cleaner', '$13.99', 'Kaboom.jpg', 'A powerful cleaner that eliminates bathroom stains, buildups and soap scum, no hard scrubbing necessary.', 'Tired of tough scrubbing? Try Kaboom Foam-Tastic, a foaming cleaner that changes color to let you know the surface is clean. As it sprays on blue, the OxiClean stain-fighting bubbles get to work powering through soap scum, hard water, and calcium and lime stains. When it turns white you just wipe away to reveal a deep clean and a sparkling shine with absolutely no dull residue left behind. See the clean! Kaboom Foam-Tastic will not stain surfaces and it leaves behind a citrus scent. No hard scrubbing. Kaboom!… and the stain is Gone !', null, '2.6 x 2.6 x 9.5 inches ; 1.4 pounds');
