INSERT INTO product (id, product_name, product_price, product_image, product_description1, product_description2, product_description3, product_dimensions) VALUES (20, 'Pressure Joe 1450-PSI Electric Pressure Washer', '$84.99', 'Pressure_washer.jpg', 'A cleaner for light to medium jobs, using the power of water.', 'The ultimate dirt fighter, the Pressure Joe SPX1000 is ideal for tackling a variety of light-to-medium duty cleaning jobs. Use it to clean boats, motorcycles, RV''s, ATV''s, trailers, decks, barbecues, siding and more. Packed with a powerful 1400-watt motor, Pressure Joe delivers up to 1450 PSI of cleaning power and a maximum flow rate of 1.45 GPM, which means you''ll get your cleaning chore done faster and easier with less strain and fatigue. Extend your cleaning reach with its 33-inch wand, which features an adjustable spray nozzle to easily change the water pressure from an intense, zero-degree jet spray to a gentle, 45-degree fan spray. Plus, the Pressure Joe SPX1000 is lightweight and compact so you can easily tote or wheel it from one cleaning task to the next. Safety features include a trigger safety lock with TSS (Total Stop System) technology and a complete on/off switch. The Pressure Joe SPX1000 is backed by a full two-year warranty and is CSA listed. GET EQUIPPED this spring with the Sun Joe Pressure Joe SPX1000—your smart solution for home, outdoor and auto cleaning projects.', null, '12 x 9.8 x 19 inches ; 11.7 pounds');
INSERT INTO product (id, product_name, product_price, product_image, product_description1, product_description2, product_description3, product_dimensions) VALUES (21, 'Rodent Sheriff', '$7.95', 'rodent_sherrif.jpg', 'Uses natural peppermin oil to get rid of mice, rats and other pests.', 'A powerful spray made in the USA from a handcrafted peppermint formula. You’ll love its long-lasting minty fresh scent, but mice, roaches, raccoons and other rodents hate it. So the minute they get a whiff, they head for the hills. And since it’s a natural formula, you can even spray it around your garden, around trash cans, vehicles, garages, storage areas, attics, entry points around your house... or anywhere these unwanted critters hang out. Each 8oz bottle contains 1,000 sprays!', null, '10 x 4.2 x 3.5 inches ; 1.5 pounds');
